import Link from 'next/link'
import Head from 'next/head'
import { useRouter } from 'next/router'

export default function Layout({ children }) {
    const router = useRouter()

    return(
        <main>
            <Head>
                <link rel="icon" href="/favicon.ico" />
                <link rel="preload" href="/fonts/OpenSans-Regular.ttf" as="font" crossOrigin="" />
                <link rel="preload" href="/fonts/OpenSans-Bold.ttf" as="font" crossOrigin="" />

            </Head>
            <div className="header">
                <div className="sizer">
                    <div className="logo">
                        <Link href="/"><a>
                            <img src="/logo.svg" alt="DFX logo" />
                        </a></Link>
                    </div>
                    <div className="nav">
                        <ul>
                            <li className={router.pathname === '/' ? 'active' : ''}><Link href="/"><a></a></Link></li>
                        </ul>
                    </div>
                </div>
            </div>
            <div className="content">
                <div className="sizer">
                    {children}
                </div>
            </div>
            <div className="footer">
                <span>Defirex</span>
            </div>

            <style global jsx>{`
                body {
                    padding: 0px;
                    margin: 0px;
                    font-family: 'Open Sans', sans-serif;
                    background-color: #F3F5F7;
                }
            `}</style>
            <style jsx>{`
            .header {
                background-color: #F01716;
                height: 60px;
                text-align: center;
                box-shadow: 0 2px 10px rgba(151,164,175,.1);
            }
            .header > .sizer {
                text-align: left;
                width: 1150px !important;
            }
            .logo {
                width: 200px;
                display: inline-block;
            }
            .logo > a > img {
                width: 175px;
                margin-top: 17px;
                transition: 50ms ease-in-out;
            }
            .logo > a:hover > img {
                opacity: 0.8;
            }
            .nav {
                width: calc(100% - 200px);
                display: inline-block;
                vertical-align: top;
                text-align: right;
            }
            .nav > ul {
                list-style-type: none;
                margin: 0px;
                padding: 0px;
            }
            .nav > ul > li {
                height: 60px;
                display: inline-block;
                padding: 0px 10px;
                margin: 0 7.5px;
                border-bottom: 2px solid transparent;
                line-height: 60px;
            }
            .active > a {
                color: #fff !important;
            }
            .nav > ul > li > a {
                text-decoration: none;
                color: #fab7b7;
                font-weight: 700;
                display: inline-block;
                transition: 75ms ease-in;
            }
            .nav > ul > li:hover > a {
                color: #fff;
            }
            .content {
                background-color: #F3F5F7;
                min-height: calc(100vh - 60px);
                text-align: center;
            }
            .footer {
                text-align: center;
                background-color: #F3F5F7;
                padding: 20px 0px 20px 0px;
            }
            .footer > span > a {
                color: #F01716;
            }
            .sizer {
                display: inline-block;
            }
            @media screen and (min-width: 1300px) {
                .sizer {
                    width: 1200px;
                }
            }
            @media screen and (min-width: 600px) and (max-width: 1300px) {
                .sizer {
                    width: 90vw;
                }
                .header > .sizer {
                    width: calc(90vw - 50px) !important;
                }
            }
            @media screen and (max-width: 600px) {
                .sizer {
                    width: 95vw;
                }
                .header > .sizer {
                    width: calc(95vw - 50px) !important;
                }
            }
            `}</style>
        </main>
    )
}
