// Setup
import Head from 'next/head'; // Update website title to include Compound
import Layout from '../components/Layout'; // Import website layout

// Card grid imports
import SmallCard from '../components/SmallCard'; // Small detail cards
import WideCard from '../components/WideCard'; // 2/3 display cards
import MidCard from '../components/MidCard'; // 1/3 display cards
import XWideCard from '../components/XWideCard'; // 3/3 display cards

// Rendered item imports


import BeatLoader from "react-spinners/BeatLoader"; // Loading animation
import COMPChart from '../components/COMPChart'; // COMP/USD chart
import TransactionList from '../components/Transaction';

// Data retrieval
import useSWR from 'swr'; // Next SWR
import fetch from 'unfetch'; // Fetch library

// Setup SWR data fetching
const fetcher = url => fetch(url).then(r => r.json())

export default function DFX() {
  const { data: data_info } = useSWR('/api/compound/info', fetcher); // General COMP info,  pulled on load, once.
  const { data: data_transaction } = useSWR('/api/compound/transaction', fetcher); // COMP markets info,  pulled on load, once.
  const { data: data_candles } = useSWR("/api/compound/chart", fetcher); // COMP/USD chart data, pulled on load, once.

  return (
    <div className="container">
      <Head>
        <title>Stats | DFX</title>
      </Head>
      <Layout>
        <div>
          <p className="data-retrieved"><span className="status-light"></span>Данные загружаются в реальном времени</p>
        </div>
        <div>
          <SmallCard name="Цена DFX токена" content={data_info ? "$" + data_info.current_price : <CustomLoader />} />
          <SmallCard name="Рыночная капитализация" content={data_info ? "$" + (data_info.market_cap).toLocaleString() : <CustomLoader />} />
          <SmallCard name="Суточный оборот" content={data_info ? "$" + parseInt(data_info.total_volume).toLocaleString() : <CustomLoader />}/>
        </div>

        <div>
          <XWideCard name="COMP/USD (Coinbase)">
            {data_candles ? <COMPChart data={data_candles} /> : <CustomLoader />}
          </XWideCard>
        </div>
        <div>
          <XWideCard name="Транзакции">
            {data_candles ? <TransactionList data={data_transaction} /> : <CustomLoader />}
          </XWideCard>
        </div>
      </Layout>
      <style global jsx>{`
      .centerize {
        width: 100%;
        height: 100%;
        text-align: center;
      }
      .centerize > div {
        transform: translateY(200px);
      }
      `}</style>

      <style jsx>{`
      .data-retrieved {
        display: inline-block;
        width: calc(100% - 55px);
        margin-block-end: 0px;
        background-color: #fff;
        height: 40px;
        border-radius: 5px;
        border: 1px solid #e7eaf3;
        box-shadow: 0 0 35px rgba(127,150,174,.125);
        line-height: 40px;
      }
      .status-light {
        height: 8px;
        width: 8px;
        display: inline-block;
        border-radius: 50%;
        transform: scale(1);
        vertical-align: middle;
        margin-right: 5px;
        margin-top: -2px;
        background-color: rgb(0, 190, 0);
        box-shadow: 0 0 0 0 rgba(0, 190, 0, 1);
        animation: pulsegreen 2s infinite;
      }
      @media screen and (max-width: 600px) {
        .data-retrieved {
          width: calc(100% - 40px);
        }
      }
      @keyframes pulsegreen {
        0% {
          transform: scale(0.95);
          box-shadow: 0 0 0 0 rgba(0, 190, 0, 0.7);
        }
      
        70% {
          transform: scale(1);
          box-shadow: 0 0 0 5px rgba(0, 190, 0, 0);
        }
      
        100% {
          transform: scale(0.95);
          box-shadow: 0 0 0 0 rgba(0, 190, 0, 0);
        }
      }
      `}</style>

    </div>
  )
}

// Loading animation
function CustomLoader() {
  return <BeatLoader
    size={10}
    color={"#F01716"}
    loading={true}
  />
}
