import ReactTable from 'react-table-6'


export default function TransactionList(props) {

    function From(props) {

        return(
            <>
                <div>
                    <div>
                        {props.from}
                    </div>

                </div>
            </>
        )
    }

    function To(props) {

        return(
            <>
                <div>
                    <div>
                        {props.to}
                    </div>
                </div>
            </>
        )
    }

    function GasUsed(props) {

        return(
            <>
                <div>
                    <div>
                        {props.gasUsed}
                    </div>

                </div>
            </>
        )
    }

    const columns = [
        {Header: 'Адрес отправителя', Accessor: 'From', Cell: row => <From {...row.original}/>},
        {Header: 'Адрес получателя', Accessor: 'To', Cell: row => <To {...row.original}/>},
        {Header: 'Комиссия', Accessor: 'gasUsed', Cell: row => <GasUsed {...row.original}/>},

    ];

    return (
        <>
            <ReactTable
                data={props.data}
                columns={columns}
                showPagination={false}
                defaultPageSize={props.data.length}
                resizable={false}
                className="comp-table"
            />

            <style global jsx>{`
            .token-distribution {
                width: calc(100% - 80px);
                margin: 20px;
                padding: 20px;
                background-color: rgba(214, 2, 44, 0.1);
                border: 1px solid rgb(214, 2, 44);
                border-radius: 5px;
                text-align: center;
            }
            .token-distribution > span {
                font-weight: bold;
                text-decoration: underline;
            }
            .token-distribution > div {
                display: inline-block;
                padding: 5px;
            }
            .token-distribution > div > label {
                display: block;
            }
            .token-distribution > div > input {
                display: block;
                height: 30px;
                margin-top: 5px;
                border: 1px solid #ccc;
                border-radius: 5px;
                text-align: center;
                font-size: 17px;
                width: 160px;
            }
            .-header {
                height: 30px;
                border-bottom: 1px solid #e7eaf3;
                box-shadow: none !important;
            }
            .-header > .rt-tr {
                font-size: 13px;
                font-weight: bold;
                color: #AAB8C1;
                line-height: 30px;
            }
            .rt-tbody, .rt-thead {
                min-width: 1055px !important;
            }
            .-header > .rt-tr > .rt-th:nth-of-type(1) > div {
                padding-left: 25px;
            }
            .-loading {
                display: none;
            }
            .-even {
                background-color: #FDFDFD;
            }
            .rt-th {
                text-align: left;
            }
            .rt-th, .rt-td {
                border-right: none !important;
            }
            .rt-td {
                border-bottom: none;
            }
            .rt-tr > .rt-th:nth-of-type(1), .rt-tr > .rt-td:nth-of-type(1) {
                width: 275px !important;
            }
            .rt-tr > .rt-th:nth-of-type(2), .rt-tr > .rt-td:nth-of-type(2) {
                width: 100px !important;
            }
            .rt-tr > .rt-th:nth-of-type(3), .rt-tr > .rt-td:nth-of-type(3) {
                width: 90px !important;
            }
            .rt-tr > .rt-th:nth-of-type(4), .rt-tr > .rt-td:nth-of-type(4) {
                width: 200px !important;
            }
            .rt-tr > .rt-th:nth-of-type(5), .rt-tr > .rt-td:nth-of-type(5) {
                width: 100px !important;
            }
            .rt-tr > .rt-th:nth-of-type(6), .rt-tr > .rt-td:nth-of-type(6) {
                width: 90px !important;
            }
            .rt-tr > .rt-th:nth-of-type(7), .rt-tr > .rt-td:nth-of-type(7) {
                width: 200px !important;
            }
            .rt-tr > .rt-td {
                padding: 0px;
            }
            .comp-table {
                border: none;
            }
            .ReactTable .rt-tbody .rt-tr-group:last-child {
                border-bottom: 1px solid rgba(0,0,0,.05);
            }
            `}</style>
        </>
    )
}
